// Get the value of the "output" field
const output = $input.first().json.output[0]

// Get the text message
const output_message = output.content[0].text

return {
  "article": output_message
}

assignments.value:
={{ 
$json.messages.map(message => {
  let role

  switch(message.id[2]) {
    case "SystemMessage":
      role = "system"
      break;
    case "HumanMessage":
      role = "user"
      break;
    case "AIMessage":
      role = "assistant"
      break;
  }

  return {role: role, content: message.kwargs.content}
})
}}

