// Get the request body
const requestBody = $input.first().json.body

return {
  "article": requestBody.article,
  "evaluationRresult": requestBody.evaluation_result
}

// Get the scoring result
const evaluationResult = $input.first().json

const article = $("Get Refined Article").first().json.article

return {
  "success": true,
  "executionId": "0",
  "data": {
    "evaluationResult": {
      "conciseness": evaluationResult.result.evaluation.conciseness,
      "coherence": evaluationResult.result.evaluation.coherence,
      "feedback": evaluationResult.result.feedback
    },
    "refinedArticle": article
  }
}
